(function($){$(function(){
  var $audioMasterTime = $('audio.mastertime');
  function checkMasterTime() {
    $.ajax({
      url: 'index.php',
      data: {'stream_mastertime':1},
      success: function(content) {
        content = JSON.parse(content);
        if(content.mastertime >= $audioMasterTime.data('from') && content.mastertime <= $audioMasterTime.data('to'))
          window.location.reload();
      },
    });
  }

  $('[name="stream_switch"]').change(function(){
    var $el = $(this),
        $player = $('#player_'+$el.prop('id'));

    $player.prop('src',$el.val());
    $player[0].volume = 0.05;
    $player[0].load();
    $player[0].play();
  });

  $('audio')[0].volume = 0.05;
  $('audio.autoplay')[0].load();
  $('audio.autoplay')[0].play();

  if($audioMasterTime.length > 0) {
    if(typeof $audioMasterTime.data('from') !== 'undefined' && typeof $audioMasterTime.data('to') !== 'undefined') {
      setInterval(checkMasterTime, 600000);
    }
  }
});})(jQuery);